// src/components/Navbar.js
import React, { useState, useEffect } from 'react';
import { NavLink, useNavigate, useLocation } from 'react-router-dom';

export default function Navbar() {
    const navigate = useNavigate();
    const location = useLocation(); // Get current URL location
    const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('token'));

    // Check if we are on the public landing page
    const isLandingPage = location.pathname === '/';

    useEffect(() => {
        const handleAuthChange = () => {
            setIsLoggedIn(!!localStorage.getItem('token'));
        };
        window.addEventListener('authChange', handleAuthChange);
        return () => {
            window.removeEventListener('authChange', handleAuthChange);
        };
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user_id');
        window.dispatchEvent(new Event('authChange'));
        navigate('/login');
    };

    const getLinkClass = ({ isActive }) => (isActive ? 'active' : '');

    // Add 'transparent' class if on the landing page
    const navBarClass = isLandingPage ? "navbar transparent" : "navbar";

    return (
        <nav className={navBarClass}>
            <div className="navbar-container">
                {/* Logo links to landing page if logged out, dashboard if logged in */}
                <NavLink to={isLoggedIn ? "/dashboard" : "/"} className="navbar-brand">
                    <span>NutritionDB</span>
                </NavLink>

                <div className="navbar-links">
                    {!isLoggedIn ? (
                        // Logged Out State
                        <>
                            <NavLink to="/login" className={getLinkClass}>
                                Sign In
                            </NavLink>
                            <NavLink to="/signup" className="button button-primary">
                                Sign Up
                            </NavLink>
                        </>
                    ) : (
                        // Logged In State
                        <>
                            <NavLink to="/dashboard" className={getLinkClass}>
                                Dashboard
                            </NavLink>
                            <NavLink to="/diet-logs" className={getLinkClass}>
                                Diet Log
                            </NavLink>
                            <NavLink to="/recipes" className={getLinkClass}>
                                Recipes
                            </NavLink>
                             <NavLink to="/ingredients" className={getLinkClass}>
                                Ingredients
                            </NavLink>
                            <NavLink to="/mealplans" className={getLinkClass}>
                                Meal Plans
                            </NavLink>
                            <NavLink to="/profile" className={getLinkClass}>
                                Profile
                            </NavLink>
                            <button onClick={handleLogout} className="button button-secondary">
                                Sign Out
                            </button>
                        </>
                    )}
                </div>
            </div>
        </nav>
    );
}
