import React, { useState, useEffect } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';

export default function Navbar() {
    const navigate = useNavigate();
    // Check local storage directly for the initial state
    const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('token'));

    useEffect(() => {
        // This function will run when the 'authChange' event is triggered
        const handleAuthChange = () => {
            setIsLoggedIn(!!localStorage.getItem('token'));
        };

        // Listen for the custom event
        window.addEventListener('authChange', handleAuthChange);

        // Clean up the listener when the component unmounts
        return () => {
            window.removeEventListener('authChange', handleAuthChange);
        };
    }, []); // Empty dependency array means this runs once on mount

    const handleLogout = () => {
        // Clear user data from storage
        localStorage.removeItem('token');
        localStorage.removeItem('user_id');
        
        // Fire the custom event to update all components (like this one)
        window.dispatchEvent(new Event('authChange'));
        
        // Redirect to login
        navigate('/login');
    };

    // Helper function to apply active styles to NavLink
    const getLinkClass = ({ isActive }) => isActive ? 'nav-link active' : 'nav-link';

    return (
        <nav className="navbar">
            <div className="navbar-container">
                <NavLink to="/" className="navbar-brand">
                    <span>NutritionDB</span>
                </NavLink>

                <div className="navbar-links">
                    {!isLoggedIn ? (
                        // Logged Out State
                        <>
                            <NavLink to="/login" className={getLinkClass}>
                                Sign In
                            </NavLink>
                            <NavLink to="/signup" className="button-primary">
                                Sign Up
                            </NavLink>
                        </>
                    ) : (
                        // Logged In State
                        <>
                            <NavLink to="/recipes" className={getLinkClass}>
                                Recipes
                            </NavLink>
                            <NavLink to="/mealplans" className={getLinkClass}>
                                Meal Plans
                            </NavLink>
                            <NavLink to="/profile" className={getLinkClass}>
                                Profile
                            </NavLink>
                            <button onClick={handleLogout} className="button-secondary">
                                Sign Out
                            </button>
                        </>
                    )}
                </div>
            </div>
        </nav>
    );
}
