import React, { useState, useEffect, useCallback } from 'react';
import api from '../services/api';
import { useParams, useNavigate } from 'react-router-dom';

export default function AdminEditUserPage() {
    const { id } = useParams(); // Get the User_ID from the URL
    const navigate = useNavigate();
    
    // Get the logged-in admin's ID for the delete check
    const loggedInAdminId = parseInt(localStorage.getItem('user_id'), 10);

    const [formData, setFormData] = useState({});
    
    // --- 1. Add state for the new password ---
    const [newPassword, setNewPassword] = useState(''); 
    
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    const fetchUserData = useCallback(async () => {
        try {
            setLoading(true);
            const res = await api.get(`/admin/users/${id}`);
            const userData = res.data;
            // Format date for the input field
            setFormData({
                ...userData,
                Date_Of_Birth: userData.Date_Of_Birth ? userData.Date_Of_Birth.split('T')[0] : ''
            });
        } catch (err) {
            setError('Could not fetch user data.');
        } finally {
            setLoading(false);
        }
    }, [id]);

    useEffect(() => {
        fetchUserData();
    }, [fetchUserData]);

    const handleChange = (e) => {
        setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        setMessage('Updating...');
        setError('');

        // --- 2. Create the data object to send ---
        // We start with all the form data
        const updateData = { ...formData };

        // **Only** add the password to the request if the admin typed one in
        if (newPassword) {
            updateData.Password = newPassword;
        }

        try {
            // Send the 'updateData' object
            await api.put(`/admin/users/${id}`, updateData); 
            setMessage('User updated successfully!');
            setNewPassword(''); // Clear the password field on success
        } catch (err) {
            // --- 3. Show specific backend errors ---
            if (err.response && err.response.data.error) {
                setError(err.response.data.error);
            } else {
                setError('Failed to update user.');
            }
            setMessage('');
        }
    };

    const handleDelete = async () => {
        // --- 4. Add self-delete check ---
        if (parseInt(id, 10) === loggedInAdminId) {
            setError('You cannot delete your own account from this panel.');
            return;
        }

        if (window.confirm('Are you sure you want to permanently delete this user? This action cannot be undone.')) {
            try {
                await api.delete(`/admin/users/${id}`);
                alert('User deleted.');
                navigate('/admin-dashboard');
            } catch (err) {
                // --- 5. Show specific backend errors ---
                if (err.response && err.response.data.error) {
                    setError(err.response.data.error);
                } else {
                    setError('Failed to delete user.');
                }
            }
        }
    };

    if (loading) return <div className="container"><p>Loading user...</p></div>;
    
    return (
        <div className="container">
            <h1>Edit User: {formData.Name}</h1>
            <p>Admin panel for updating or deleting a user profile.</p>
            {/* Show error/success messages at the top */}
            {error && <p className="error-message" style={{color: 'red'}}>{error}</p>}
            {message && <p className="success-message" style={{color: 'green'}}>{message}</p>}

            <div className="detail-grid">
                <div className="detail-main">
                    <form className="form-container" style={{margin: 0, width: '100%'}} onSubmit={handleUpdate}>
                        <h3>User Details</h3>
                        
                        <div className="form-group">
                            <label>Name</label>
                            <input type="text" name="Name" value={formData.Name || ''} onChange={handleChange} />
                        </div>
                        <div className="form-group">
                            <label>Email</label>
                            <input type="email" name="Email" value={formData.Email || ''} onChange={handleChange} />
                        </div>
                        
                        <div className="form-group">
                            <label>Role (Permission)</label>
                            <select name="role" value={formData.role || 'user'} onChange={handleChange}>
                                <option value="user">User</option>
                                <option value="admin">Admin</option>
                            </select>
                        </div>

                        {/* --- 6. Add Password Reset Field --- */}
                        <div className="form-group">
                            <label>Reset Password (Optional)</label>
                            <input 
                                type="password" 
                                name="Password" 
                                value={newPassword}
                                onChange={(e) => setNewPassword(e.target.value)}
                                placeholder="Enter new password to reset"
                            />
                        </div>
                        {/* ---------------------------------- */}
                        
                        <div className="form-group">
                            <label>Date of Birth</label>
                            <input type="date" name="Date_Of_Birth" value={formData.Date_Of_Birth || ''} onChange={handleChange} />
                        </div>
                        <div className="form-group">
                            <label>Height (cm)</label>
                            <input type="number" name="Height_cm" value={formData.Height_cm || ''} onChange={handleChange} />
                        </div>
                        <div className="form-group">
                            <label>Weight (kg)</label>
                            <input type="number" name="Weight_kg" value={formData.Weight_kg || ''} onChange={handleChange} />
                        </div>
                        <div className="form-group">
                            <label>Activity Level</label>
                            <select name="Activity_Level" value={formData.Activity_Level || 'Moderate'} onChange={handleChange}>
                                <option value="Sedentary">Sedentary</option>
                                <option value="Light">Light</option>
                                <option value="Moderate">Moderate</option>
                                <option value="Active">Active</option>
                                <option value="Very Active">Very Active</option>
                            </select>
                        </div>
                        <div className="form-group">
                            <label>Dietary Preferences</label>
                            <input type="text" name="Dietary_Preferences" value={formData.Dietary_Preferences || ''} onChange={handleChange} />
                        </div>
                        <div className="form-group">
                            <label>Allergies</label>
                            <input type="text" name="Allergies" value={formData.Allergies || ''} onChange={handleChange} />
                        </div>
                        
                        <button type="submit" className="form-button">Save Changes</button>
                    </form>
                </div>
                <div className="detail-sidebar">
                    <h3>Danger Zone</h3>
                    <p>This action is permanent and cannot be undone.</p>
                    <button onClick={handleDelete} className="button button-danger">Delete This User</button>
                </div>
            </div>
        </div>
    );
}
