import React, { useState } from 'react';
import api from '../services/api'; // Use our central API helper
import { useNavigate, Link } from 'react-router-dom';

export default function LoginPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');

        try {
            // Use 'api.post' - no full URL or headers needed!
            const response = await api.post('/login', {
                Email: email,
                Password: password
            });

            if (response.data.access_token) {
                // Save token AND user_id from our updated backend
                localStorage.setItem('token', response.data.access_token);
                localStorage.setItem('user_id', response.data.user_id);
                
                // Fire a global event to tell the Navbar to update
                window.dispatchEvent(new Event('authChange'));
                
                // Redirect to the main recipe page
                navigate('/recipes'); 
            }
        } catch (err) {
            if (err.response && err.response.data) {
                setError(err.response.data.error || 'Login failed!');
            } else {
                setError('Login failed. Please try again.');
            }
        }
    };

    return (
        <div className="form-container">
            <h2 className="form-title">Sign In</h2>
            <form onSubmit={handleLogin}>
                <div className="form-group">
                    <label htmlFor="email">Email address</label>
                    <input
                        id="email" type="email" required
                        value={email} onChange={(e) => setEmail(e.target.value)}
                        placeholder="you@example.com"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input
                        id="password" type="password" required
                        value={password} onChange={(e) => setPassword(e.target.value)}
                        placeholder="••••••••"
                    />
                </div>

                {error && <p className="error-message">{error}</p>}

                <div className="form-group">
                    <button type="submit" className="form-button">
                        Sign In
                    </button>
                </div>
            </form>
            <p className="form-text-link">
                Don't have an account?{' '}
                <Link to="/signup">Sign up</Link>
            </p>
        </div>
    );
}
