import React, { useState } from 'react';
import api from '../services/api';
import { useNavigate, Link } from 'react-router-dom';

export default function SignupPage() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSignup = async (e) => {
        e.preventDefault();
        setError('');

        try {
            // 1. Create the new user
            // Note: The backend is now hardcoded to make this user an ADMIN
            await api.post('/users', {
                Name: name,
                Email: email,
                Password: password,
                // Default values for required fields to prevent DB errors
                Date_Of_Birth: '2000-01-01', 
                Height_cm: 170,
                Weight_kg: 70
            });
            
            // 2. Automatically log them in
            const loginResponse = await api.post('/login', {
                Email: email,
                Password: password
            });

            if (loginResponse.data.access_token) {
                // 3. Save ALL their credentials (Token, ID, and Role)
                localStorage.setItem('token', loginResponse.data.access_token);
                localStorage.setItem('user_id', loginResponse.data.user_id);
                
                // IMPORTANT: Save the role returned by the backend ('admin')
                localStorage.setItem('role', loginResponse.data.role);
                
                // 4. Fire the auth event to update the navbar immediately
                window.dispatchEvent(new Event('authChange'));
                
                // 5. Redirect straight to the Admin Dashboard
                navigate('/admin-dashboard', { replace: true });
            }

        } catch (err) {
            console.error(err);
            if (err.response && err.response.data) {
                setError(err.response.data.error || 'Signup failed!');
            } else {
                setError('Signup failed. Please try again.');
            }
        }
    };

    return (
        <div className="form-container">
            <h2 className="form-title">Create Admin Account</h2>
            <form onSubmit={handleSignup}>
                <div className="form-group">
                    <label>Name</label>
                    <input
                        type="text" required value={name} onChange={(e) => setName(e.target.value)}
                        placeholder="John Doe"
                    />
                </div>
                <div className="form-group">
                    <label>Email</label>
                    <input
                        type="email" required value={email} onChange={(e) => setEmail(e.target.value)}
                        placeholder="you@example.com"
                    />
                </div>
                <div className="form-group">
                    <label>Password</label>
                    <input
                        type="password" required value={password} onChange={(e) => setPassword(e.target.value)}
                        placeholder="••••••••"
                    />
                </div>

                {error && <p className="error-message">{error}</p>}

                <div className="form-group">
                    <button type="submit" className="form-button">
                        Sign Up & Go to Admin Panel
                    </button>
                </div>
            </form>
            <p className="form-text-link">
                Already have an account? <Link to="/login">Sign in</Link>
            </p>
        </div>
    );
}
