import React, { useState, useEffect, useCallback } from 'react';
import api from '../services/api'; // Assuming 'api' is your pre-configured axios instance
import { Link } from 'react-router-dom';

export default function AdminDashboardPage() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // --- 1. Get the logged-in admin's ID from local storage ---
    // We parse it as an integer for safe comparison
    const loggedInAdminId = parseInt(localStorage.getItem('user_id'), 10);

    const fetchAllUsers = useCallback(async () => {
        try {
            setLoading(true);
            const response = await api.get('/admin/users');
            setUsers(response.data);
        } catch (err) {
            if (err.response && err.response.status === 403) {
                setError("You do not have permission to view this page.");
            } else {
                setError("Could not fetch user data.");
            }
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchAllUsers();
    }, [fetchAllUsers]);

    // --- 2. Add the delete handler function ---
    const handleDeleteUser = async (userIdToDelete) => {
        // Double-check the admin doesn't delete themselves (even if button wasn't disabled)
        if (userIdToDelete === loggedInAdminId) {
            alert("You cannot delete your own account.");
            return;
        }

        if (window.confirm('Are you sure you want to permanently delete this user?')) {
            try {
                // Call the admin delete endpoint
                await api.delete(`/admin/users/${userIdToDelete}`);
                
                // On success, remove the user from the state to update the UI
                setUsers(prevUsers => prevUsers.filter(user => user.User_ID !== userIdToDelete));
                
            } catch (err) {
                // --- 3. Show specific error messages from the backend ---
                if (err.response && err.response.data.error) {
                    // This will show errors like "Admin cannot delete their own account."
                    alert(`Error: ${err.response.data.error}`);
                } else {
                    // Generic fallback
                    alert('An unknown error occurred while deleting the user.');
                }
            }
        }
    };

    if (loading) return <div className="container"><p>Loading admin panel...</p></div>;
    if (error) return <div className="container"><p className="error-message">{error}</p></div>;

    return (
        <div className="container">
            <h1>Admin Dashboard</h1>
            <p>This panel displays all users currently in the database.</p>

            <div style={{ overflowX: 'auto', marginTop: '2rem' }}>
                <table className="table">
                    <thead>
                        <tr>
                            <th>User ID</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Joined On</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user.User_ID}>
                                <td>{user.User_ID}</td>
                                <td>{user.Name}</td>
                                <td>{user.Email}</td>
                                <td>
                                    <span 
                                        className="card-tag" 
                                        style={{
                                            backgroundColor: user.role === 'admin' ? '#fecaca' : '#dcfce7',
                                            color: user.role === 'admin' ? '#991b1b' : '#166534'
                                        }}
                                    >
                                        {user.role}
                                    </span>
                                </td>
                                <td>{new Date(user.Created_At).toLocaleDateString()}</td>
                                <td>
                                    <Link 
                                        to={`/admin/edit-user/${user.User_ID}`} 
                                        className="button button-secondary"
                                        style={{fontSize: '0.75rem', padding: '0.25rem 0.5rem', marginRight: '0.5rem'}}
                                    >
                                        Edit
                                    </Link>
                                    
                                    {/* --- 4. Add the Delete button with disabled check --- */}
                                    <button
                                        onClick={() => handleDeleteUser(user.User_ID)}
                                        className="button button-danger" // Assuming you have a danger/delete style
                                        style={{fontSize: '0.75rem', padding: '0.25rem 0.5rem'}}
                                        // Disable the button if the user ID matches the logged-in admin's ID
                                        disabled={user.User_ID === loggedInAdminId} 
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}
